    private void shutDown(){
        int confirmed = JOptionPane.showConfirmDialog(null, "Are you sure you "
                + "want to exit the program?", "Exit program confirmation", JOptionPane.YES_NO_OPTION);
        if (confirmed == JOptionPane.YES_OPTION) {
            System.exit(0);
        }
    }
